  <script>
    // === JavaScript ===

    // é¢æ¿æŠ˜å å‡½æ•° (ä¿æŒä¸å˜)
    function togglePanel(panelElement) {
      if (!panelElement) return;
      const content = panelElement.querySelector('.panel-content');
      const header = panelElement.querySelector('.panel-header');
      const icon = header ? header.querySelector('.toggle-icon') : null;

      if (!header || !header.hasAttribute('data-panel-toggle') || !content || content.classList.contains('animating')) return;

      content.classList.add('animating');
      const isCollapsed = content.classList.contains('collapsed');

      if (isCollapsed) { // Expand
        header.classList.remove('collapsed');
