        content.style.maxHeight = '0';
        content.style.paddingTop = '0';
        content.style.paddingBottom = '0';
        content.style.opacity = '0';
        content.style.borderTopColor = 'transparent';
        if (icon) icon.style.transform = 'rotate(0deg)';
