      }
    }

    // å‘é€æ¶ˆæ¯å‡½æ•° (æ ¸å¿ƒï¼šå‘é€ /send <message>|/trigger)
    function sendMessage(message) {
      // Escape backticks and dollar signs potentially problematic in template literals or commands
      const escapedMessage = message.replace(/`/g, '\\`').replace(/\$/g, '\\$');
      // Construct the command: send user message then trigger AI response
      const commandToSend = `/send ${escapedMessage}|/trigger`; // The desired format
      try {
        // Check if triggerSlash is available and execute
        if (typeof triggerSlash === 'function') {
          triggerSlash(commandToSend);
          console.log(`Sent via triggerSlash: ${commandToSend}`);
        } else {
          console.warn('triggerSlash function is not available in this context.');
          // Fallback or alternative method could be placed here if needed
        }
      } catch (e) {
        console.error("Error executing triggerSlash:", e);
      }
      // Log the attempt regardless for debugging
      console.log(`Attempted command: ${commandToSend}`);
    }

    // ç¡®è®¤ç®€å•æ“ä½œï¼ˆæ— è¾“å…¥ï¼‰çš„å‡½æ•°
    function confirmAction(actionMessage) {
      sendMessage(actionMessage); // Uses the standard send+trigger format
      const openModal = document.querySelector('.modal.active');
      if (openModal) closeModal(openModal.id);
    }

    // å¤„ç†å¸¦è¾“å…¥çš„æ“ä½œï¼ˆç›®å‰ä»…ç”¨äºåˆ†è§£ï¼‰
    function handleDecomposeAction(inputId, commandPrefix) {
        const inputElement = document.getElementById(inputId);
