            inputElement.focus();
        }
    }


    // æ»šåŠ¨åŠ¨ç”»å‡½æ•° (ä¿æŒä¸å˜)
    function animateOnScroll() {
      const elementsToAnimate = document.querySelectorAll('.panel, .system-message-block, .scene-block');
      const observer = new IntersectionObserver((entries) => {
        entries.forEach(entry => {
          if (entry.isIntersecting) {
            entry.target.classList.add('panel-visible');
            observer.unobserve(entry.target);
          }
        });
      }, {
        threshold: 0.1
      });

      elementsToAnimate.forEach(el => {
        el.classList.add('panel-animated');
        observer.observe(el);
      });
    }

    // æ›´æ–°UIå‡½æ•° (ä¿æŒä¸å˜)
    function updateUIWithPlaceholders(placeholders) {
