      // MODIFIED: Modal ç¡®è®¤æŒ‰é’®äº‹ä»¶ - Handles different actions
      const confirmButtons = document.querySelectorAll('.modal-button.confirm');
      confirmButtons.forEach(button => {
        button.addEventListener('click', function () {
          const action = this.dataset.action; // For recruit modal (simple action)
          const actionType = this.dataset.actionType; // For 'continue' modal
