    /* Header Styles */
    header {
      display: flex;
      justify-content: space-between;
      align-items: center;
      padding: 15px 20px;
      margin-bottom: 25px;
      position: relative;
      background: linear-gradient(90deg, rgba(26, 22, 58, 0.3) 0%, rgba(26, 22, 58, 0.8) 50%, rgba(26, 22, 58, 0.3) 100%);
      border-radius: 15px;
      box-shadow: 0 5px 15px rgba(0, 0, 0, 0.3);
      backdrop-filter: var(--glass-effect);
    }

    header::before {
      content: '';
      position: absolute;
      top: 0;
      left: 0;
      right: 0;
      height: 1px;
      background: linear-gradient(90deg, transparent, var(--primary), transparent);
      opacity: 0.6;
    }

    header h1 {
      font-size: 28px;
      font-weight: 700;
      color: var(--primary);
      text-shadow: var(--glow-primary);
      letter-spacing: 2px;
      position: relative;
      display: inline-block;
    }

    header h1::after {
      content: '';
      position: absolute;
      bottom: -5px;
      left: 0;
      width: 100%;
      height: 2px;
      background: linear-gradient(90deg, transparent, var(--primary), transparent);
      box-shadow: 0 0 5px var(--primary);
    }

    .resources {
      display: flex;
      gap: 20px;
      font-size: 18px;
    }

    .resource {
      display: flex;
      align-items: center;
      gap: 10px;
      padding: 8px 15px;
      background: rgba(32, 28, 68, 0.4);
      border-radius: 20px;
      box-shadow: 0 0 10px rgba(160, 240, 248, 0.2);
      transition: var(--transition-normal);
      backdrop-filter: blur(5px);
    }

    .resource:hover {
      transform: translateY(-2px);
      box-shadow: 0 5px 15px rgba(160, 240, 248, 0.3);
    }

    .resource-icon {
      width: 30px;
      height: 30px;
      border-radius: 50%;
      background: radial-gradient(circle, rgba(160, 240, 248, 0.7) 0%, rgba(214, 191, 255, 0.3) 100%);
      display: flex;
      align-items: center;
      justify-content: center;
      box-shadow: 0 0 10px rgba(160, 240, 248, 0.5);
      font-size: 16px;
    }

    .resource span {
      font-weight: 500;
      letter-spacing: 0.5px;
    }

    /* Navigation Button Styles */
    .navigation {
      display: flex;
      flex-wrap: wrap;
      gap: 12px;
      padding: 18px 25px;
      border-radius: var(--border-radius);
      justify-content: center;
    }

    .nav-btn {
      flex: 1 1 auto;
      min-width: 110px;
      padding: 12px 20px;
      text-transform: uppercase;
      border-radius: 8px;
      font-size: 14px;
      font-weight: 700;
      color: var(--text-secondary);
      text-shadow: 0 1px 2px rgba(0, 0, 0, 0.2);
      cursor: pointer;
      border: none;
      transition: all 0.3s ease;
      user-select: none;
      letter-spacing: 1px;
      display: flex;
      align-items: center;
      justify-content: center;
      gap: 8px;
      position: relative;
      overflow: hidden;
      z-index: 1;
      background: rgba(255, 255, 255, 0.08);
      border: 1px solid rgba(255, 255, 255, 0.1);
    }

    .navigation .nav-btn:nth-child(1) {
      background: linear-gradient(135deg, rgba(255, 153, 214, 0.18), rgba(214, 191, 255, 0.12));
    }

    .navigation .nav-btn:nth-child(2) {
      background: linear-gradient(135deg, rgba(214, 191, 255, 0.18), rgba(255, 119, 204, 0.12));
    }

    .navigation .nav-btn:nth-child(3) {
      background: linear-gradient(135deg, rgba(255, 119, 204, 0.18), rgba(255, 153, 214, 0.12));
    }

    .nav-btn i {
      font-size: 16px;
      margin-right: 5px;
      transition: transform 0.3s ease, color 0.3s ease;
      color: var(--accent-purple);
      opacity: 0.9;
    }

    .nav-btn:hover i {
      transform: scale(1.2);
      color: var(--accent-pink);
    }

    .nav-btn::before {
      content: "";
      position: absolute;
      top: 0;
      left: 0;
      right: 0;
      bottom: 0;
      background: linear-gradient(135deg, var(--accent-pink), var(--accent-purple));
      opacity: 0;
      transition: opacity 0.35s ease;
      z-index: -1;
    }

    .nav-btn:hover::before,
    .nav-btn:focus::before {
      opacity: 0.85;
    }

    .nav-btn:hover,
    .nav-btn:focus {
      color: #ffffff;
      text-shadow: 0 0 8px var(--accent-pink);
      box-shadow: 0 6px 20px var(--shadow-color-soft), 0 0 15px var(--accent-pink);
      transform: translateY(-3px);
      border-color: rgba(255, 255, 255, 0.2);
    }

    .nav-btn:active {
      transform: translateY(-1px);
      box-shadow: 0 3px 10px var(--shadow-color-soft);
    }

    /* Probability Display */
    .probability-display {
      display: flex;
      flex-wrap: wrap;
      justify-content: center;
      gap: 10px;
      /* Added gap */
      margin-bottom: 20px;
      /* Added margin */
    }

    .probability-item {
      padding: 8px 15px;
      background: rgba(32, 28, 68, 0.5);
      border-radius: 15px;
      font-size: 14px;
      display: flex;
      align-items: center;
      gap: 5px;
      backdrop-filter: blur(5px);
      box-shadow: 0 3px 10px rgba(0, 0, 0, 0.2);
      transition: var(--transition-normal);
    }

    .probability-item:hover {
      transform: translateY(-2px);
      box-shadow: 0 5px 15px rgba(0, 0, 0, 0.3), 0 0 10px rgba(160, 240, 248, 0.3);
    }

    .star-rating {
      color: gold;
      letter-spacing: 1px;
      text-shadow: 0 0 5px rgba(255, 215, 0, 0.5);
    }

    /* Summon Area */
    .summon-area {
      display: flex;
      flex-direction: column;
      align-items: center;
      gap: 30px;
      position: relative;
      padding: 40px 0;
      margin-bottom: 40px;
    }

    /* --- NEW ANIMATION CSS STARTS --- */
    .mainWrap {
      /* Removed absolute positioning. Centering is handled by summon-area */
      perspective: 1000px;
      /* Perspective for 3D effect */
      transform: scale(1.1);
      /* Adjust scale as needed for the summon area */
      transform-origin: center center;
      margin-bottom: 20px;
      /* Spacing below the animation */
      cursor: pointer;
      /* Make it clear it's clickable */
      transition: transform 0.3s ease;
    }

    .mainWrap:hover {
      transform: scale(1.15);
      /* Slightly larger on hover */
    }

    .mainWrap div {
      transform-origin: center center;
    }

    .wrapper {
      position: relative;
    }

    .c1 {
      border-radius: 100%;
      height: 200px;
      width: 200px;
      border: 1px solid #e7b439;
      animation:
        rotFirst 30s linear infinite,
        fadeIn 2s forwards;
      transform-style: preserve-3d;
    }

    .c1 .c2 {
      position: absolute;
      top: 50%;
      left: 50%;
      transform: translate(-50%, -50%);
      border-radius: 100%;
      height: 190px;
      width: 190px;
      border: 1px solid #ffb61e;
      border-style: dashed;
    }

    .c1 .c2 .c3 {
      position: absolute;
      top: 50%;
      left: 50%;
      transform: translate(-50%, -50%);
      border: 1px solid #ffb61e;
      height: 180px;
      width: 180px;
      border-radius: 100%;
      animation: changeColor 30s linear infinite alternate;
      /* Added alternate */
    }

    .c1 .c2 .c3 .rect1 {
      border: 1px solid #ffb61e;
      border-style: dotted;
      position: absolute;
      top: 50%;
      left: 50%;
      transform: translate(-50%, -50%);
      height: 120px;
      width: 120px;
    }

    .c1 .c2 .c3 .rect1 .miniC {
      text-align: center;
      height: 120px;
      width: 120px;
      position: relative;
    }

    .c1 .c2 .c3 .rect1 .miniC>div[class^="miniC"] {
      position: absolute;
      height: 20px;
      width: 20px;
      border: 3px solid #ffb61e;
      border-radius: 100%;
      animation: changeColor 30s linear infinite alternate;
      /* Added alternate */
      transform-origin: center center;
    }

    .c1 .c2 .c3 .rect1 .miniC .miniC1 {
      top: -10px;
      left: 50%;
      transform: translateX(-50%);
    }

    .c1 .c2 .c3 .rect1 .miniC .miniC2 {
      top: 50%;
      left: -10px;
      transform: translateY(-50%);
    }

    .c1 .c2 .c3 .rect1 .miniC .miniC3 {
      top: 50%;
      right: -10px;
      left: auto;
      transform: translateY(-50%);
    }

    .c1 .c2 .c3 .rect1 .miniC .miniC4 {
      bottom: -10px;
      top: auto;
      left: 50%;
      transform: translateX(-50%);
    }

    .c1 .c2 .c3 .rect1 .c4 {
      border: 1px solid #956eff;
      height: 120px;
      width: 120px;
      position: absolute;
      top: 50%;
      left: 50%;
      transform: translate(-50%, -50%);
      border-radius: 100%;
      border-style: dotted;
      animation: changeColor 30s linear infinite alternate;
      /* Added alternate */
    }

    .c1 .c2 .c3 .rect1 .c4 .rect2 {
      border: 1px solid #956eff;
      height: 80px;
      width: 80px;
      position: absolute;
      top: 50%;
      left: 50%;
      transform: translate(-50%, -50%);
      animation: rotminiC 10s linear infinite;
    }

    .c1 .c2 .c3 .rect1 .c4 .rect3 {
      border: 1px solid #956eff;
      height: 80px;
      width: 80px;
      transform: rotate(135deg);
      transform-origin: center center;
    }

    .c1 .c2 .c3 .rect1 .c5 {
      border: 1px solid #956eff;
      height: 70px;
      width: 70px;
      position: absolute;
      top: 50%;
      left: 50%;
      transform: translate(-50%, -50%);
      border-radius: 100%;
      animation: changeColor 30s linear infinite alternate;
      /* Added alternate */
    }

    .c1 .c2 .c3 .rect1 .c6 {
      border: 3px solid #956eff;
      height: 50px;
      width: 50px;
      animation: changeColor 30s linear infinite alternate;
      /* Added alternate */
      position: absolute;
      top: 50%;
      left: 50%;
      transform: translate(-50%, -50%);
      border-radius: 100%;
    }

    /* Hover State for new animation */
    .c1:hover,
    .c1 .c2:hover,
    .c1 .c2 .c3:hover {
      box-shadow: 0 0 20px #956eff;
      /* Removed cursor: pointer here, applied to mainWrap */
      transition: transform 0.5s ease-in-out, box-shadow 0.5s ease-in-out;
    }

    /* Keyframes for new animation */
    @keyframes rotFirst {
      0% {
        transform: rotate(0deg);
      }

      100% {
        transform: rotate(360deg);
      }
    }

    @keyframes rotminiC {
      0% {
        transform: translate(-50%, -50%) rotate(0deg);
      }

      100% {
        transform: translate(-50%, -50%) rotate(-360deg);
      }
    }

    @keyframes changeColor {
      0% {
        box-shadow: 0px 0px 70px #eca4e9;
        border-color: #a193f1;
      }

      100% {
        box-shadow: 0px 0px 30px #60a5ff;
        border-color: #88bfff;
      }

      /* Adjusted end state for alternate */
    }

    @keyframes fadeIn {
      0% {
        opacity: 0;
        transform: scale(0.8);
      }

      100% {
        opacity: 1;
        transform: scale(1);
      }

      /* Scale is handled by mainWrap now */
    }

    /* --- NEW ANIMATION CSS ENDS --- */


    .summon-btn {
      padding: 15px 40px;
      background: linear-gradient(45deg, var(--primary-dark), var(--accent-purple));
      border: none;
      border-radius: 30px;
      color: white;
      font-size: 18px;
      font-weight: 600;
      cursor: pointer;
      transition: var(--transition-bounce);
      box-shadow: 0 5px 20px rgba(0, 0, 0, 0.3), var(--glow-primary);
      position: relative;
      overflow: hidden;
      letter-spacing: 1px;
      display: flex;
      align-items: center;
      gap: 10px;
      z-index: 1;
    }

    .summon-btn i {
      font-size: 20px;
      transition: transform 0.3s ease;
    }

    .summon-btn:hover i {
      transform: rotate(180deg);
    }

    .summon-btn::before {
      content: '';
      position: absolute;
      top: -5px;
      left: -5px;
      right: -5px;
      bottom: -5px;
      background: linear-gradient(45deg, var(--primary), var(--accent-purple), var(--primary));
      background-size: 200% 200%;
      z-index: -1;
      border-radius: 35px;
      opacity: 0;
      transition: opacity 0.3s ease;
      animation: gradient-animation 3s infinite;
    }

    @keyframes gradient-animation {
      0% {
        background-position: 0% 50%;
      }

      50% {
        background-position: 100% 50%;
      }

      100% {
        background-position: 0% 50%;
      }
    }

    .summon-btn:hover::before {
      opacity: 1;
    }

    .summon-btn:hover {
      transform: translateY(-5px);
      box-shadow: 0 10px 25px rgba(0, 0, 0, 0.4), var(--glow-primary);
    }

    .summon-btn:active {
      transform: translateY(-2px);
      box-shadow: 0 5px 15px rgba(0, 0, 0, 0.3);
    }

    /* Result Area */
    .result-area {
      width: 100%;
      max-width: 900px;
      background: var(--bg-panel);
      border-radius: 15px;
      padding: 30px;
      margin: 0 auto;
      box-shadow: 0 10px 30px rgba(0, 0, 0, 0.5), 0 0 20px rgba(160, 240, 248, 0.3);
      opacity: 0;
      transform: translateY(20px);
      transition: opacity 1s ease, transform 1s ease;
      position: relative;
      overflow: hidden;
      backdrop-filter: var(--glass-effect);
      display: none;
      /* Initially hidden */
    }

    .result-area.show {
      opacity: 1;
      transform: translateY(0);
      display: block;
    }

    .result-area::before {
      content: '';
      position: absolute;
      top: 0;
      left: 0;
      width: 100%;
      height: 5px;
      background: linear-gradient(90deg, var(--primary-dark), var(--primary), var(--primary-dark));
      box-shadow: var(--glow-primary);
    }

    .system-message {
      font-size: 18px;
      color: var(--primary-light);
      margin-bottom: 30px;
      font-style: italic;
      line-height: 1.6;
      text-align: center;
      padding: 20px;
      background: rgba(32, 28, 68, 0.5);
      border-radius: 10px;
      position: relative;
      overflow: hidden;
      box-shadow: 0 5px 15px rgba(0, 0, 0, 0.2);
    }

    .system-message::before {
      content: '';
      position: absolute;
      top: 0;
      left: 0;
      width: 100%;
      height: 100%;
      background: linear-gradient(135deg, rgba(160, 240, 248, 0.1) 0%, transparent 100%);
      z-index: -1;
    }

    .system-message i {
      color: var(--primary);
      margin-right: 8px;
    }

    /* Character Card */
    .character-card {
      display: flex;
      flex-direction: column;
      gap: 20px;
      margin-top: 30px;
      position: relative;
      background: var(--bg-card);
      border-radius: 15px;
      padding: 25px;
      box-shadow: 0 8px 25px rgba(0, 0, 0, 0.3);
      overflow: hidden;
    }

    .character-header {
      display: flex;
      justify-content: space-between;
      align-items: center;
      padding-bottom: 15px;
      border-bottom: 1px solid rgba(160, 240, 248, 0.3);
    }

    .character-name-container {
      display: flex;
      align-items: center;
      gap: 10px;
    }

    .character-name {
      font-size: 28px;
      font-weight: bold;
      color: var(--primary-light);
      text-shadow: var(--glow-primary);
    }

    .character-rarity {
      color: var(--accent-purple);
      letter-spacing: 3px;
      text-shadow: var(--glow-accent);
      display: block;
      text-align: center;
      flex-grow: 1;
    }

    .character-species {
      font-size: 16px;
      color: var(--text-light);
      margin-bottom: 10px;
      display: flex;
      align-items: center;
      gap: 10px;
    }

    .character-species i {
      color: var(--secondary);
    }

    .character-background {
      font-size: 15px;
      line-height: 1.7;
      color: #f8f8f2;
      margin-bottom: 20px;
      padding: 15px;
      background: rgba(32, 28, 68, 0.5);
      border-radius: 10px;
      position: relative;
      box-shadow: inset 0 0 10px rgba(0, 0, 0, 0.2);
    }

    .info-section {
      margin-bottom: 20px;
      animation: fade-in 0.5s ease forwards;
      opacity: 0;
      transform: translateY(10px);
    }

    .info-section:nth-child(1) {
      animation-delay: 0.1s;
    }

    .info-section:nth-child(2) {
      animation-delay: 0.2s;
    }

    .info-section:nth-child(3) {
      animation-delay: 0.3s;
    }

    .info-section:nth-child(4) {
      animation-delay: 0.4s;
    }

    .info-section:nth-child(5) {
      animation-delay: 0.5s;
    }

    @keyframes fade-in {
      to {
        opacity: 1;
        transform: translateY(0);
      }
    }

    .info-title {
      font-size: 17px;
      font-weight: bold;
      color: var(--primary-light);
      margin-bottom: 10px;
      border-bottom: 1px solid rgba(160, 240, 248, 0.4);
      padding-bottom: 5px;
      display: flex;
      align-items: center;
      gap: 8px;
    }

    .info-title i {
      color: var(--primary);
    }

    .info-content {
      font-size: 15px;
      line-height: 1.7;
      color: var(--text-light);
      padding: 10px 15px;
      background: rgba(32, 28, 68, 0.4);
      border-radius: 8px;
    }

    .skill-list {
      display: flex;
      flex-wrap: wrap;
      gap: 10px;
      margin-top: 10px;
      padding-left: 0;
      list-style: none;
    }

    .skill-list li {
      padding: 8px 15px;
      background: linear-gradient(135deg, rgba(32, 28, 68, 0.6), rgba(26, 22, 58, 0.6));
      border-radius: 20px;
      font-size: 14px;
      white-space: nowrap;
      display: inline-flex;
      align-items: center;
      gap: 5px;
      box-shadow: 0 3px 10px rgba(0, 0, 0, 0.2);
      transition: var(--transition-normal);
    }

    .skill-list li:hover {
      transform: translateY(-3px);
      box-shadow: 0 5px 15px rgba(0, 0, 0, 0.3), 0 0 10px rgba(160, 240, 248, 0.3);
      background: linear-gradient(135deg, rgba(32, 28, 68, 0.8), rgba(26, 22, 58, 0.8));
    }

    .quote {
      font-style: italic;
      color: var(--primary-light);
      text-align: center;
      padding: 20px;
      margin-top: 20px;
      border-radius: 10px;
      background: rgba(32, 28, 68, 0.3);
      position: relative;
      box-shadow: 0 5px 15px rgba(0, 0, 0, 0.2);
    }

    .quote::before {
      content: '\f10d';
      font-family: 'Font Awesome 6 Free';
      font-weight: 900;
      position: absolute;
      top: 10px;
      left: 10px;
      color: rgba(160, 240, 248, 0.4);
      font-size: 20px;
    }

    .quote::after {
      content: '\f10e';
      font-family: 'Font Awesome 6 Free';
      font-weight: 900;
      position: absolute;
      bottom: 10px;
      right: 10px;
      color: rgba(160, 240, 248, 0.4);
      font-size: 20px;
    }

    /* Action Buttons */
    .action-buttons {
      display: flex;
      gap: 15px;
      margin-top: 25px;
      justify-content: center;
      flex-wrap: wrap;
    }

    .action-btn {
      padding: 12px 25px;
      background: linear-gradient(135deg, rgba(32, 28, 68, 0.6), rgba(26, 22, 58, 0.6));
      border-radius: 25px;
      border: 1px solid rgba(160, 240, 248, 0.2);
      color: var(--text-light);
      cursor: pointer;
      transition: var(--transition-normal);
      font-size: 15px;
      font-weight: 500;
      display: flex;
      align-items: center;
      gap: 8px;
      box-shadow: 0 3px 10px rgba(0, 0, 0, 0.2);
    }

    .action-btn i {
      font-size: 16px;
      color: var(--primary);
    }

    .action-btn:hover {
      background: linear-gradient(135deg, rgba(32, 28, 68, 0.8), rgba(26, 22, 58, 0.8));
      transform: translateY(-3px);
      box-shadow: 0 5px 15px rgba(0, 0, 0, 0.3), var(--glow-primary);
      color: var(--primary-light);
    }

    .action-btn:active {
      transform: translateY(-1px);
      box-shadow: 0 3px 10px rgba(0, 0, 0, 0.2);
    }

    .action-btn.copied {
      background: linear-gradient(135deg, #4caf50, #81c784);
      color: white !important;
      border-color: transparent;
    }

    .action-btn.copied:hover {
      background: linear-gradient(135deg, #4caf50, #81c784);
      transform: translateY(-3px);
      box-shadow: 0 5px 15px rgba(0, 0, 0, 0.3), 0 0 10px #4caf50;
    }

    /* Modal Styles */
    .modal {
      display: none;
      position: fixed;
      inset: 0;
      background-color: rgba(28, 24, 58, 0.96);
      z-index: 1000;
      justify-content: center;
      align-items: center;
      padding: 15px;
      backdrop-filter: blur(10px);
      transition: opacity 0.35s ease;
      opacity: 0;
    }

    .modal.active {
      display: flex;
      opacity: 1;
    }

    .modal-content {
      background: linear-gradient(145deg, rgba(50, 40, 80, 0.98), rgba(40, 30, 70, 0.98));
      box-shadow: 0 18px 45px rgba(0, 0, 0, 0.5), 0 0 30px var(--accent-pink);
      border-radius: var(--border-radius);
      width: 90%;
      max-width: 480px;
      padding: 30px;
      position: relative;
      overflow: hidden;
      transform: scale(0.9);
      opacity: 0;
      transition: transform 0.35s cubic-bezier(0.175, 0.885, 0.32, 1.275), opacity 0.35s ease;
      border: 1px solid rgba(255, 255, 255, 0.15);
    }

    .modal.active .modal-content {
      transform: scale(1);
      opacity: 1;
    }

    .modal-content::before {
      content: "";
      position: absolute;
      width: 250%;
      height: 250%;
      top: -75%;
      left: -75%;
      z-index: -1;
      background: radial-gradient(circle, rgba(255, 153, 214, 0.15) 0%, transparent 65%);
      animation: rotate 24s linear infinite;
    }

    .modal-header {
      display: flex;
      justify-content: space-between;
      align-items: center;
      margin-bottom: 25px;
      border-bottom: 1px solid var(--border-color-soft);
      padding-bottom: 18px;
      position: relative;
    }

    .modal-header::after {
      content: "";
      position: absolute;
      bottom: -1px;
      left: 10%;
      width: 80%;
      height: 2px;
      background: linear-gradient(to right, transparent, var(--accent-pink), var(--accent-purple), transparent);
      opacity: 0.9;
      filter: blur(2px);
    }

    .modal-header h3 {
      margin: 0;
      color: var(--accent-pink);
      font-size: 20px;
      text-shadow: 0 0 10px var(--accent-pink);
      letter-spacing: 0.8px;
      position: relative;
      display: inline-block;
    }

    .close-modal {
      background: none;
      border: none;
      color: var(--text-secondary);
      font-size: 30px;
      cursor: pointer;
      line-height: 1;
      padding: 0 5px;
      transition: all 0.3s ease;
      position: relative;
      z-index: 2;
      text-shadow: 0 0 3px rgba(0, 0, 0, 0.5);
    }

    .close-modal:hover {
      color: var(--accent-pink);
      transform: rotate(90deg) scale(1.2);
      text-shadow: 0 0 8px var(--accent-pink);
    }

    .modal-body p {
      margin-bottom: 18px;
      color: var(--text-secondary);
      font-size: 15px;
      line-height: 1.7;
      letter-spacing: 0.4px;
    }

    .modal-input {
      width: 100%;
      padding: 14px 20px;
      margin-bottom: 25px;
      border-radius: 8px;
      border: none;
      background-color: rgba(45, 35, 65, 0.85);
      color: var(--text-primary);
      font-size: 16px;
      box-shadow: inset 0 2px 5px rgba(0, 0, 0, 0.35);
      transition: all 0.3s ease;
      font-family: 'Segoe UI', system-ui, sans-serif;
      border: 1px solid transparent;
      text-shadow: none;
    }

    .modal-input:focus {
      outline: none;
      box-shadow: inset 0 2px 5px rgba(0, 0, 0, 0.35), 0 0 12px var(--accent-pink);
      background-color: rgba(55, 40, 75, 0.9);
      border-color: var(--accent-pink);
    }

    .modal-buttons {
      display: flex;
      justify-content: flex-end;
      gap: 15px;
      margin-top: 10px;
    }

    .modal-button {
      padding: 12px 25px;
      border-radius: 20px;
      border: none;
      cursor: pointer;
      font-weight: 700;
      font-size: 14px;
      text-transform: uppercase;
      transition: all 0.3s ease;
      letter-spacing: 1px;
      background: rgba(255, 255, 255, 0.15);
      color: var(--text-secondary);
      box-shadow: 0 3px 8px rgba(0, 0, 0, 0.25);
      border: 1px solid rgba(255, 255, 255, 0.15);
      text-shadow: none;
    }

    .modal-button.confirm {
      background: linear-gradient(135deg, var(--accent-pink), var(--accent-purple));
      color: #1f152f;
      border: 1px solid rgba(255, 255, 255, 0.2);
    }

    .modal-button:hover,
    .modal-button:focus {
      filter: brightness(1.15);
      transform: translateY(-2px);
      box-shadow: 0 5px 15px rgba(0, 0, 0, 0.3);
    }

    .modal-button.confirm:hover,
    .modal-button.confirm:focus {
      box-shadow: 0 5px 15px rgba(0, 0, 0, 0.3), 0 0 15px var(--accent-pink);
    }

    .modal-button.cancel:hover,
    .modal-button.cancel:focus {
      background: rgba(255, 255, 255, 0.2);
      color: var(--text-primary);
    }

    .modal-button:active {
      transform: translateY(0px);
      filter: brightness(0.9);
      box-shadow: 0 2px 5px rgba(0, 0, 0, 0.2);
    }

    .input-error {
      animation: shake 0.5s cubic-bezier(.36, .07, .19, .97) both;
      border: 1px solid var(--danger-color) !important;
      box-shadow: inset 0 2px 5px rgba(0, 0, 0, 0.35), 0 0 10px var(--danger-color) !important;
    }

    @keyframes shake {

      10%,
      90% {
        transform: translateX(-1px);
      }

      20%,
      80% {
        transform: translateX(2px);
      }

      30%,
      50%,
      70% {
        transform: translateX(-3px);
      }

      40%,
      60% {
        transform: translateX(3px);
      }
    }

    @keyframes rotate {
      from {
        transform: rotate(0deg);
      }

      to {
        transform: rotate(360deg);
      }
    }

    /* Toast notification */
    .toast {
      position: fixed;
      top: 20px;
      left: 50%;
      transform: translateX(-50%) translateY(-100px);
      background: rgba(32, 28, 68, 0.95);
      color: var(--text-light);
      padding: 12px 25px;
      border-radius: 30px;
      box-shadow: 0 5px 20px rgba(0, 0, 0, 0.3), var(--glow-primary);
      display: flex;
      align-items: center;
      gap: 10px;
      z-index: 1000;
      transition: transform 0.5s cubic-bezier(0.175, 0.885, 0.32, 1.275);
      backdrop-filter: blur(10px);
      border: 1px solid rgba(160, 240, 248, 0.3);
    }

    .toast.show {
      transform: translateX(-50%) translateY(0);
    }

    .toast i {
      color: var(--primary);
    }

    /* Other Effects */
    .floating-bubble {
      position: absolute;
      border-radius: 50%;
      background: radial-gradient(circle, rgba(255, 255, 255, 0.8) 0%, rgba(160, 240, 248, 0.3) 100%);
      box-shadow: 0 0 5px rgba(160, 240, 248, 0.5);
      pointer-events: none;
      z-index: 2;
      opacity: 0.7;
      animation: float-bubble var(--float-duration) ease-in-out infinite alternate;
    }

    @keyframes float-bubble {
      0% {
        transform: translateY(0) translateX(0);
      }

      100% {
        transform: translateY(calc(-1 * var(--float-distance))) translateX(var(--float-side));
      }
    }

    .explosion-particle {
      position: absolute;
      background: radial-gradient(circle, var(--primary) 0%, transparent 70%);
      border-radius: 50%;
      pointer-events: none;
      z-index: 100;
      opacity: 0.8;
    }

    .pulse {
      animation: pulse 2s infinite;
    }

    @keyframes pulse {
      0% {
        box-shadow: 0 0 0 0 rgba(160, 240, 248, 0.7);
      }

      70% {
        box-shadow: 0 0 0 15px rgba(160, 240, 248, 0);
      }

      100% {
        box-shadow: 0 0 0 0 rgba(160, 240, 248, 0);
      }
    }

    .glow-text {
      text-shadow: 0 0 8px var(--primary);
    }

    .ripple {
      position: absolute;
      border-radius: 50%;
      transform: scale(0);
      animation: ripple 0.6s linear;
      background-color: rgba(255, 255, 255, 0.3);
    }

    @keyframes ripple {
      to {
        transform: scale(4);
        opacity: 0;
      }
    }

    /* Responsive adjustments */
    @media (max-width: 900px) {
      .action-buttons {
        justify-content: center;
      }
    }

    @media (max-width: 768px) {
      .navigation {
        flex-direction: column;
        align-items: stretch;
        padding: 15px;
      }

      .nav-btn {
        width: auto;
      }

      header {
        flex-direction: column;
        gap: 15px;
        align-items: center;
      }

      .result-area {
        padding: 20px 15px;
      }

      .system-message {
        font-size: 16px;
        padding: 15px;
      }

      .character-header {
        flex-direction: column;
        align-items: flex-start;
        gap: 5px;
      }

      .character-rarity {
        text-align: left;
        margin-top: 5px;
      }
    }

    /* Content Entry Animation Styles */
    .result-area .info-section,
    .result-area .quote,
    .result-area .action-buttons {
      transition: opacity 0.6s ease-out, transform 0.6s ease-out;
    }

    .animate-in {
      opacity: 1 !important;
      transform: translateY(0) !important;
    }
  </style>
</head>

