  <script>
    // --- Core Helper Functions (Keep as is) ---
    function showModal(type) { const modalId = `${type}-modal`; const modal = document.getElementById(modalId); if (modal) { if (type === 'interact') { const charNameElement = document.getElementById('placeholder-2'); const modalCharNameElement = document.getElementById('interact-char-name'); if (charNameElement && modalCharNameElement) { modalCharNameElement.textContent = charNameElement.textContent || 'è§’è‰²'; } } modal.style.display = 'flex'; setTimeout(() => { modal.classList.add('active'); const input = modal.querySelector('input[type="text"]'); if (input) setTimeout(() => input.focus(), 50); }, 10); } else { console.error(`Modal with ID ${modalId} not found.`); } }
    function closeModal(modalId) { const modal = document.getElementById(modalId); if (modal) { modal.classList.remove('active'); setTimeout(() => { modal.style.display = 'none'; }, 350); } }
    function createRipple(event) { const button = event.currentTarget; let existingRipple = button.querySelector('.ripple'); if (existingRipple) { existingRipple.remove(); } const ripple = document.createElement('span'); const rect = button.getBoundingClientRect(); const size = Math.max(rect.width, rect.height) * 1.5; const x = event.clientX - rect.left - size / 2; const y = event.clientY - rect.top - size / 2; ripple.style.width = ripple.style.height = `${size}px`; ripple.style.left = `${x}px`; ripple.style.top = `${y}px`; ripple.classList.add('ripple'); button.appendChild(ripple); setTimeout(() => { ripple.remove(); }, 600); }
    function sendMessage(message) { const escapedMessage = message.replace(/`/g, '\\`').replace(/\$/g, '\\$'); const isLikelyCommand = message.startsWith('/') || message.includes('|') || message.includes('{{') || message.includes('key='); const commandToSend = isLikelyCommand ? escapedMessage : `/send ${escapedMessage}|/trigger`; try { if (typeof triggerSlash === 'function') { triggerSlash(commandToSend); console.log(`Sent via triggerSlash: ${commandToSend}`); } else { console.warn('triggerSlash function not available. Simulating send:'); console.log(`Command: ${commandToSend}`); } } catch (e) { console.error("Error sending command via triggerSlash:", e); } }
    function confirmAction(action) { sendMessage(action); const openModal = document.querySelector('.modal.active'); if (openModal) closeModal(openModal.id); }
    function confirmActionWithInput(actionPrefix, inputId, charNamePlaceholderId) { const inputElement = document.getElementById(inputId); const modal = inputElement.closest('.modal'); if (!inputElement) { console.error(`Input element with ID ${inputId} not found.`); if (modal) closeModal(modal.id); return; } const inputValue = inputElement.value.trim(); if (inputValue) { let message = `${actionPrefix}ï¼š${inputValue}`; if (charNamePlaceholderId) { const charNameElement = document.getElementById('placeholder-2'); const charName = charNameElement ? charNameElement.textContent.trim() : 'è§’è‰²'; message = message.replace(/ä¸.*?äº’åŠ¨/, `ä¸ ${charName} äº’åŠ¨`); } sendMessage(message); inputElement.value = ''; if (modal) closeModal(modal.id); inputElement.classList.remove('input-error'); } else { inputElement.classList.add('input-error'); setTimeout(() => { inputElement.classList.remove('input-error'); }, 500); inputElement.focus(); } }

    // --- Visual Effects and Animations (Keep as is) ---
    function showToast(message, duration = 3000) { const toast = document.getElementById('toast'); const toastMessage = document.getElementById('toast-message'); if (!toast || !toastMessage) return; toastMessage.textContent = message; toast.classList.add('show'); if (toast.timerId) { clearTimeout(toast.timerId); } toast.timerId = setTimeout(() => { toast.classList.remove('show'); toast.timerId = null; }, duration); }
    function createBubbles() { const container = document.body; const numBubbles = 30; if (container.querySelectorAll('.floating-bubble').length > numBubbles * 2) return; for (let i = 0; i < numBubbles; i++) { const bubble = document.createElement('div'); bubble.classList.add('floating-bubble'); const size = Math.random() * 30 + 5; bubble.style.width = `${size}px`; bubble.style.height = `${size}px`; const posX = Math.random() * window.innerWidth; const startY = window.innerHeight + Math.random() * 100; bubble.style.left = `${posX}px`; bubble.style.top = `${startY}px`; bubble.style.opacity = Math.random() * 0.5 + 0.1; bubble.style.setProperty('--float-duration', `${Math.random() * 8 + 7}s`); bubble.style.setProperty('--float-distance', `${Math.random() * 80 + 60}px`); bubble.style.setProperty('--float-side', `${(Math.random() - 0.5) * 60}px`); container.appendChild(bubble); animateBubbleUp(bubble); } }
    function animateBubbleUp(bubble) { const speed = Math.random() * 1 + 0.3; let currentTop = parseFloat(bubble.style.top); function frame() { currentTop -= speed; bubble.style.top = `${currentTop}px`; if (currentTop < -50) { bubble.remove(); } else { requestAnimationFrame(frame); } } requestAnimationFrame(frame); }
    function createParticles() { const container = document.body; const numParticles = 50; if (container.querySelectorAll('.particle').length > numParticles * 2) return; for (let i = 0; i < numParticles; i++) { const particle = document.createElement('div'); particle.classList.add('particle'); const size = Math.random() * 3 + 1; particle.style.width = `${size}px`; particle.style.height = `${size}px`; const posX = Math.random() * window.innerWidth; const posY = Math.random() * window.innerHeight; particle.style.left = `${posX}px`; particle.style.top = `${posY}px`; particle.style.animationDelay = `${Math.random() * 15}s`; particle.style.setProperty('--angle', `${Math.random() * 360}deg`); container.appendChild(particle); setTimeout(() => particle.remove(), 20000); } }
    function createExplosion(x, y) { const numParticles = 100; const container = document.createElement('div'); container.style.cssText = 'position:fixed; left:0; top:0; width:100vw; height:100vh; pointer-events:none; z-index:1000;'; document.body.appendChild(container); for (let i = 0; i < numParticles; i++) { const particle = document.createElement('div'); particle.classList.add('explosion-particle'); const size = Math.random() * 25 + 8; particle.style.width = `${size}px`; particle.style.height = `${size}px`; const colors = ['#8be9fd', '#ff79c6', '#bd93f9', '#f1fa8c', '#ffffff']; const color = colors[Math.floor(Math.random() * colors.length)]; particle.style.background = `radial-gradient(circle, ${color} 0%, transparent 70%)`; particle.style.boxShadow = `0 0 ${Math.random() * 10 + 5}px ${color}`; particle.style.position = 'absolute'; particle.style.left = `${x}px`; particle.style.top = `${y}px`; const angle = Math.random() * Math.PI * 2; const distance = Math.random() * 250 + 80; const duration = Math.random() * 1.2 + 0.6; const delay = Math.random() * 0.1; particle.style.transition = `all ${duration}s cubic-bezier(0.1, 0.8, 0.2, 1) ${delay}s`; container.appendChild(particle); requestAnimationFrame(() => { requestAnimationFrame(() => { particle.style.transform = `translate(${Math.cos(angle) * distance}px, ${Math.sin(angle) * distance}px) scale(0)`; particle.style.opacity = '0'; }); }); setTimeout(() => { particle.remove(); }, (duration + delay) * 1000 + 100); } setTimeout(() => { container.remove(); }, 2500); }

    // --- MODIFIED: summonAnimation function ---
    function summonAnimation() {
      const animationContainer = document.querySelector('.mainWrap'); // Target the new animation container
      const resultArea = document.querySelector('.result-area');
      const summonBtn = document.querySelector('.summon-btn');

      if (!animationContainer || !resultArea || !summonBtn) {
        console.error("Summon animation elements not found!");
        return;
      }

      // Prevent multiple listeners
      if (summonBtn.dataset.listenerAttached) return;
      summonBtn.dataset.listenerAttached = 'true';

      summonBtn.addEventListener('click', function (e) {
        createRipple(e);
