      width: 20px;
      height: 20px;
      border-radius: 50%;
      background: #fff;
      z-index: 10;
      filter: blur(2px);
    }

    .bubble span {
      position: absolute;
      border-radius: 50%;
    }

    .bubble span:nth-child(1) {
      inset: 10px;
      border-left: 15px solid var(--primary);
      filter: blur(8px);
    }

    .bubble span:nth-child(2) {
      inset: 10px;
      border-right: 15px solid var(--secondary);
      filter: blur(8px);
    }

    .bubble span:nth-child(3) {
      inset: 10px;
      border-top: 15px solid var(--accent);
      filter: blur(8px);
    }

    .bubble span:nth-child(4) {
      inset: 30px;
      border-left: 15px solid var(--secondary);
      filter: blur(12px);
    }

    .bubble span:nth-child(5) {
      inset: 10px;
      border-bottom: 10px solid #fff;
      filter: blur(8px);
      transform: rotate(330deg);
    }

    .spinner {
      background-image: linear-gradient(var(--accent-purple) 35%, var(--primary));
      width: 100px;
      height: 100px;
      animation: spinning82341 8s linear infinite;
      text-align: center;
      border-radius: 50px;
      filter: blur(1px);
      box-shadow: 0px -5px 20px 0px var(--accent-purple), 0px 5px 20px 0px var(--primary);
      position: absolute;
      z-index: 2;
    }

    .spinner1 {
      background-color: var(--bg-dark);
      width: 80px;
      height: 80px;
      border-radius: 50px;
      filter: blur(10px);
      position: absolute;
      top: 10px;
      left: 10px;
    }

    @keyframes spinning82341 {
      to {
        transform: rotate(360deg);
      }
    }

    .crystal-energy {
      position: absolute;
      width: 120px;
      height: 120px;
      border-radius: 50%;
      background: radial-gradient(circle, rgba(160, 240, 248, 0.4) 0%, rgba(214, 191, 255, 0.2) 100%);
      box-shadow: 0 0 30px rgba(160, 240, 248, 0.5), inset 0 0 20px rgba(255, 153, 214, 0.3);
      animation: pulse-energy 4s infinite alternate;
      z-index: 1;
    }

    @keyframes pulse-energy {
      0% {
        box-shadow: 0 0 30px rgba(160, 240, 248, 0.5), inset 0 0 20px rgba(255, 153, 214, 0.3);
        transform: scale(1);
      }

      100% {
        box-shadow: 0 0 50px rgba(160, 240, 248, 0.7), inset 0 0 30px rgba(255, 153, 214, 0.5);
        transform: scale(1.05);
      }
    }

    .crystal-runes {
      position: absolute;
      width: 200px;
      height: 200px;
      border-radius: 50%;
      border: 2px dotted rgba(160, 240, 248, 0.4);
      animation: rotate-runes 20s linear infinite;
      z-index: 0;
    }

    .crystal-runes::before,
    .crystal-runes::after {
      content: '';
      position: absolute;
      width: 10px;
      height: 10px;
      background: var(--primary);
      border-radius: 50%;
      top: 50%;
      left: -5px;
      box-shadow: 0 0 10px var(--primary);
    }

    .crystal-runes::after {
      left: auto;
      right: -5px;
      background: var(--secondary);
      box-shadow: 0 0 10px var(--secondary);
    }

    @keyframes rotate-runes {
      0% {
        transform: rotate(0deg);
      }

      100% {
        transform: rotate(360deg);
      }
    }

    .summon-btn {
      padding: 15px 40px;
      background: linear-gradient(45deg, var(--primary-dark), var(--accent-purple));
      border: none;
      border-radius: 30px;
      color: white;
      font-size: 18px;
      font-weight: 600;
      cursor: pointer;
      transition: var(--transition-bounce);
      box-shadow: 0 5px 20px rgba(0, 0, 0, 0.3), var(--glow-primary);
      position: relative;
      overflow: hidden;
      letter-spacing: 1px;
      display: flex;
      align-items: center;
      gap: 10px;
      z-index: 1;
    }

    .summon-btn i {
      font-size: 20px;
      transition: transform 0.3s ease;
    }

    .summon-btn:hover i {
      transform: rotate(180deg);
    }

    .summon-btn::before {
      content: '';
      position: absolute;
      top: -5px;
      left: -5px;
      right: -5px;
      bottom: -5px;
      background: linear-gradient(45deg, var(--primary), var(--accent-purple), var(--primary));
      background-size: 200% 200%;
      z-index: -1;
      border-radius: 35px;
      opacity: 0;
      transition: opacity 0.3s ease;
      animation: gradient-animation 3s infinite;
    }

    @keyframes gradient-animation {
      0% {
        background-position: 0% 50%;
      }

      50% {
        background-position: 100% 50%;
      }

      100% {
        background-position: 0% 50%;
      }
    }

    .summon-btn:hover::before {
      opacity: 1;
    }

    .summon-btn:hover {
      transform: translateY(-5px);
      box-shadow: 0 10px 25px rgba(0, 0, 0, 0.4), var(--glow-primary);
    }

    .summon-btn:active {
      transform: translateY(-2px);
      box-shadow: 0 5px 15px rgba(0, 0, 0, 0.3);
    }

    /* Result Area */
    .result-area {
      width: 100%;
      max-width: 900px;
      background: var(--bg-panel);
      border-radius: 15px;
      padding: 30px;
      margin: 0 auto;
      box-shadow: 0 10px 30px rgba(0, 0, 0, 0.5), 0 0 20px rgba(160, 240, 248, 0.3);
      display: none;
      /* Initially hidden */
      opacity: 0;
      transform: translateY(20px);
      transition: opacity 0.7s ease, transform 0.7s ease;
      /* Smoother transition */
      position: relative;
      overflow: hidden;
      backdrop-filter: var(--glass-effect);
    }

    .result-area.show {
      /* Renamed class for clarity */
      display: block;
      /* Set display when shown */
      opacity: 1;
      transform: translateY(0);
    }

    .result-area::before {
      content: '';
      position: absolute;
      top: 0;
      left: 0;
      width: 100%;
      height: 5px;
      background: linear-gradient(90deg, var(--primary-dark), var(--primary), var(--primary-dark));
      box-shadow: var(--glow-primary);
    }

    .system-message {
      font-size: 18px;
      color: var(--primary-light);
      margin-bottom: 30px;
      font-style: italic;
      line-height: 1.6;
      text-align: center;
      padding: 20px;
      background: rgba(32, 28, 68, 0.5);
      border-radius: 10px;
      position: relative;
      overflow: hidden;
      box-shadow: 0 5px 15px rgba(0, 0, 0, 0.2);
    }

    .system-message::before {
      content: '';
      position: absolute;
      top: 0;
      left: 0;
      width: 100%;
      height: 100%;
      background: linear-gradient(135deg, rgba(160, 240, 248, 0.1) 0%, transparent 100%);
      z-index: -1;
    }

    .system-message i {
      color: var(--primary);
      margin-right: 8px;
    }

    /* Character Card */
    .character-card {
      display: flex;
      flex-direction: column;
      gap: 20px;
      margin-top: 30px;
      position: relative;
      background: var(--bg-card);
      border-radius: 15px;
      padding: 25px;
      box-shadow: 0 8px 25px rgba(0, 0, 0, 0.3);
      overflow: hidden;
    }

    .character-header {
      display: flex;
      justify-content: space-between;
      align-items: center;
      padding-bottom: 15px;
      border-bottom: 1px solid rgba(160, 240, 248, 0.3);
      flex-wrap: wrap;
      gap: 10px;
    }

    .character-name-container {
      display: flex;
      align-items: center;
      gap: 10px;
      flex-basis: 70%;
      min-width: 200px;
    }

    .character-name {
      font-size: 28px;
      font-weight: bold;
      color: var(--primary-light);
      text-shadow: var(--glow-primary);
    }

    .character-rarity {
      color: var(--accent-purple);
      letter-spacing: 3px;
      text-shadow: var(--glow-accent);
      text-align: right;
      flex-grow: 1;
      flex-basis: auto;
      min-width: 100px;
    }

    .character-species {
      font-size: 16px;
      color: var(--text-light);
      margin-bottom: 10px;
      display: flex;
      align-items: flex-start;
      /* Align items to top */
      gap: 10px;
    }

    .character-species i {
      color: var(--secondary);
      flex-shrink: 0;
      /* Prevent icon shrinking */
      margin-top: 3px;
      /* Align icon better with text */
    }

    .character-species .species-label {
      flex-shrink: 0;
      white-space: nowrap;
    }

    .character-species span[id^="placeholder-"] {
      white-space: normal;
      word-break: break-word;
      min-width: 0;
    }

    /* Allow description to wrap */

    .character-background {
      font-size: 15px;
      line-height: 1.7;
      color: #f8f8f2;
      margin-bottom: 20px;
      padding: 15px;
      background: rgba(32, 28, 68, 0.5);
      border-radius: 10px;
      position: relative;
      box-shadow: inset 0 0 10px rgba(0, 0, 0, 0.2);
      word-wrap: break-word;
    }

    .info-section {
      margin-bottom: 20px;
      opacity: 1;
      transform: translateY(0);
      /* Remove initial animation state */
    }

    /* Animation handled by JS */
    .info-title {
      font-size: 17px;
      font-weight: bold;
      color: var(--primary-light);
      margin-bottom: 10px;
      border-bottom: 1px solid rgba(160, 240, 248, 0.4);
      padding-bottom: 5px;
      display: flex;
      align-items: center;
      gap: 8px;
    }

    .info-title i {
      color: var(--primary);
    }

    .info-content {
      font-size: 15px;
      line-height: 1.7;
      color: var(--text-light);
      padding: 10px 15px;
      background: rgba(32, 28, 68, 0.4);
      border-radius: 8px;
      word-wrap: break-word;
    }

    .skill-list {
      display: flex;
      flex-wrap: wrap;
      gap: 10px;
      margin-top: 10px;
      padding-left: 0;
      list-style: none;
    }

    .skill-list li {
      padding: 8px 15px;
      background: linear-gradient(135deg, rgba(32, 28, 68, 0.6), rgba(26, 22, 58, 0.6));
      border-radius: 20px;
      font-size: 14px;
      white-space: nowrap;
      display: inline-flex;
      align-items: center;
      gap: 5px;
      box-shadow: 0 3px 10px rgba(0, 0, 0, 0.2);
      transition: var(--transition-normal);
    }

    .skill-list li:hover {
      transform: translateY(-3px);
      box-shadow: 0 5px 15px rgba(0, 0, 0, 0.3), 0 0 10px rgba(160, 240, 248, 0.3);
      background: linear-gradient(135deg, rgba(32, 28, 68, 0.8), rgba(26, 22, 58, 0.8));
    }

    .quote {
      font-style: italic;
      color: var(--primary-light);
      text-align: center;
      padding: 20px;
      margin-top: 20px;
      border-radius: 10px;
      background: rgba(32, 28, 68, 0.3);
      position: relative;
      box-shadow: 0 5px 15px rgba(0, 0, 0, 0.2);
    }

    .quote::before {
      content: '\f10d';
      font-family: 'Font Awesome 6 Free';
      font-weight: 900;
      position: absolute;
      top: 10px;
      left: 10px;
      color: rgba(160, 240, 248, 0.4);
      font-size: 20px;
    }

    .quote::after {
      content: '\f10e';
      font-family: 'Font Awesome 6 Free';
      font-weight: 900;
      position: absolute;
      bottom: 10px;
      right: 10px;
      color: rgba(160, 240, 248, 0.4);
      font-size: 20px;
    }

    /* Action Buttons */
    .action-buttons {
      display: flex;
      gap: 15px;
      margin-top: 25px;
      justify-content: center;
      flex-wrap: wrap;
    }

    .action-btn {
      padding: 12px 25px;
      background: linear-gradient(135deg, rgba(32, 28, 68, 0.6), rgba(26, 22, 58, 0.6));
      border-radius: 25px;
      border: 1px solid rgba(160, 240, 248, 0.2);
      color: var(--text-light);
      cursor: pointer;
      transition: var(--transition-normal);
      font-size: 15px;
      font-weight: 500;
      display: flex;
      align-items: center;
      gap: 8px;
      box-shadow: 0 3px 10px rgba(0, 0, 0, 0.2);
    }

    .action-btn i {
      font-size: 16px;
      color: var(--primary);
    }

    .action-btn:hover {
      background: linear-gradient(135deg, rgba(32, 28, 68, 0.8), rgba(26, 22, 58, 0.8));
      transform: translateY(-3px);
      box-shadow: 0 5px 15px rgba(0, 0, 0, 0.3), var(--glow-primary);
      color: var(--primary-light);
    }

    .action-btn:active {
      transform: translateY(-1px);
      box-shadow: 0 3px 10px rgba(0, 0, 0, 0.2);
    }

    .action-btn.copied {
      background: linear-gradient(135deg, #4caf50, #81c784);
      color: white !important;
      border-color: transparent;
    }

    .action-btn.copied:hover {
      background: linear-gradient(135deg, #4caf50, #81c784);
      transform: translateY(-3px);
      box-shadow: 0 5px 15px rgba(0, 0, 0, 0.3), 0 0 10px #4caf50;
    }

    /* Modal Styles */
    .modal {
      display: none;
      position: fixed;
      inset: 0;
      background-color: rgba(28, 24, 58, 0.96);
      z-index: 1000;
      justify-content: center;
      align-items: center;
      padding: 15px;
      backdrop-filter: blur(10px);
      transition: opacity 0.35s ease;
      opacity: 0;
    }

    .modal.active {
      display: flex;
      opacity: 1;
    }

    .modal-content {
      background: linear-gradient(145deg, rgba(50, 40, 80, 0.98), rgba(40, 30, 70, 0.98));
      box-shadow: 0 18px 45px rgba(0, 0, 0, 0.5), 0 0 30px var(--accent-pink);
      border-radius: var(--border-radius);
      width: 90%;
      max-width: 480px;
      padding: 30px;
      position: relative;
      overflow: hidden;
      transform: scale(0.9);
      opacity: 0;
      transition: transform 0.35s cubic-bezier(0.175, 0.885, 0.32, 1.275), opacity 0.35s ease;
      border: 1px solid rgba(255, 255, 255, 0.15);
    }

    .modal.active .modal-content {
      transform: scale(1);
      opacity: 1;
    }

    .modal-content::before {
      content: "";
      position: absolute;
      width: 250%;
      height: 250%;
      top: -75%;
      left: -75%;
      z-index: -1;
      background: radial-gradient(circle, rgba(255, 153, 214, 0.15) 0%, transparent 65%);
      animation: rotate 24s linear infinite;
    }

    .modal-header {
      display: flex;
      justify-content: space-between;
      align-items: center;
      margin-bottom: 25px;
      border-bottom: 1px solid var(--border-color-soft);
      padding-bottom: 18px;
      position: relative;
    }

    .modal-header::after {
      content: "";
      position: absolute;
      bottom: -1px;
      left: 10%;
      width: 80%;
      height: 2px;
      background: linear-gradient(to right, transparent, var(--accent-pink), var(--accent-purple), transparent);
      opacity: 0.9;
      filter: blur(2px);
    }

    .modal-header h3 {
      margin: 0;
      color: var(--accent-pink);
      font-size: 20px;
      text-shadow: 0 0 10px var(--accent-pink);
      letter-spacing: 0.8px;
      position: relative;
      display: inline-block;
    }

    .close-modal-btn {
      /* Renamed class for clarity in JS */
      background: none;
      border: none;
      color: var(--text-secondary);
      font-size: 30px;
      cursor: pointer;
      line-height: 1;
      padding: 0 5px;
      transition: all 0.3s ease;
      position: relative;
      z-index: 2;
      text-shadow: 0 0 3px rgba(0, 0, 0, 0.5);
    }

    .close-modal-btn:hover {
      color: var(--accent-pink);
      transform: rotate(90deg) scale(1.2);
      text-shadow: 0 0 8px var(--accent-pink);
    }

    .modal-body p {
      margin-bottom: 18px;
      color: var(--text-secondary);
      font-size: 15px;
      line-height: 1.7;
      letter-spacing: 0.4px;
    }

    .modal-input {
      width: 100%;
      padding: 14px 20px;
      margin-bottom: 25px;
      border-radius: 8px;
      border: none;
      background-color: rgba(45, 35, 65, 0.85);
      color: var(--text-primary);
      font-size: 16px;
      box-shadow: inset 0 2px 5px rgba(0, 0, 0, 0.35);
      transition: all 0.3s ease;
      font-family: 'Segoe UI', system-ui, sans-serif;
      border: 1px solid transparent;
      text-shadow: none;
    }

    .modal-input:focus {
      outline: none;
      box-shadow: inset 0 2px 5px rgba(0, 0, 0, 0.35), 0 0 12px var(--accent-pink);
      background-color: rgba(55, 40, 75, 0.9);
      border-color: var(--accent-pink);
    }

    .modal-buttons {
      display: flex;
      justify-content: flex-end;
      gap: 15px;
      margin-top: 10px;
    }

    .modal-button {
      padding: 12px 25px;
      border-radius: 20px;
      border: none;
      cursor: pointer;
      font-weight: 700;
      font-size: 14px;
      text-transform: uppercase;
      transition: all 0.3s ease;
      letter-spacing: 1px;
      background: rgba(255, 255, 255, 0.15);
      color: var(--text-secondary);
      box-shadow: 0 3px 8px rgba(0, 0, 0, 0.25);
      border: 1px solid rgba(255, 255, 255, 0.15);
      text-shadow: none;
    }

    .modal-button.confirm {
      background: linear-gradient(135deg, var(--accent-pink), var(--accent-purple));
      color: #1f152f;
      border: 1px solid rgba(255, 255, 255, 0.2);
    }

    .modal-button:hover,
    .modal-button:focus {
      filter: brightness(1.15);
      transform: translateY(-2px);
      box-shadow: 0 5px 15px rgba(0, 0, 0, 0.3);
    }

    .modal-button.confirm:hover,
    .modal-button.confirm:focus {
      box-shadow: 0 5px 15px rgba(0, 0, 0, 0.3), 0 0 15px var(--accent-pink);
    }

    .modal-button.cancel:hover,
    .modal-button.cancel:focus {
      background: rgba(255, 255, 255, 0.2);
      color: var(--text-primary);
    }

    .modal-button:active {
      transform: translateY(0px);
      filter: brightness(0.9);
      box-shadow: 0 2px 5px rgba(0, 0, 0, 0.2);
    }

    .input-error {
      animation: shake 0.5s cubic-bezier(.36, .07, .19, .97) both;
      border: 1px solid var(--danger-color) !important;
      box-shadow: inset 0 2px 5px rgba(0, 0, 0, 0.35), 0 0 10px var(--danger-color) !important;
    }

    @keyframes shake {

      10%,
      90% {
        transform: translateX(-1px);
      }

      20%,
      80% {
        transform: translateX(2px);
      }

      30%,
      50%,
      70% {
        transform: translateX(-3px);
      }

      40%,
      60% {
        transform: translateX(3px);
      }
    }

    @keyframes rotate {
      from {
        transform: rotate(0deg);
      }

      to {
        transform: rotate(360deg);
      }
    }

    /* Toast notification */
    .toast {
      position: fixed;
      top: 20px;
      left: 50%;
      transform: translateX(-50%) translateY(-100px);
      background: rgba(32, 28, 68, 0.95);
      color: var(--text-light);
      padding: 12px 25px;
      border-radius: 30px;
      box-shadow: 0 5px 20px rgba(0, 0, 0, 0.3), var(--glow-primary);
      display: flex;
      align-items: center;
      gap: 10px;
      z-index: 1000;
      transition: transform 0.5s cubic-bezier(0.175, 0.885, 0.32, 1.275);
      backdrop-filter: blur(10px);
      border: 1px solid rgba(160, 240, 248, 0.3);
      pointer-events: none;
      /* Prevent interaction */
    }

    .toast.show {
      transform: translateX(-50%) translateY(0);
    }

    .toast i {
      color: var(--primary);
    }

    /* Other Effects */
    .explosion-particle {
      position: fixed;
      /* Ensure explosion particles are relative to viewport */
      background: radial-gradient(circle, var(--primary) 0%, transparent 70%);
      border-radius: 50%;
      pointer-events: none;
      z-index: 1000;
      opacity: 0.8;
    }

    .pulse {
      animation: pulse 2s infinite;
    }

    @keyframes pulse {
      0% {
        box-shadow: 0 0 0 0 rgba(160, 240, 248, 0.7);
      }

      70% {
        box-shadow: 0 0 0 15px rgba(160, 240, 248, 0);
      }

      100% {
        box-shadow: 0 0 0 0 rgba(160, 240, 248, 0);
      }
    }

    .glow-text {
      text-shadow: 0 0 8px var(--primary);
    }

    .ripple {
      position: absolute;
      border-radius: 50%;
      transform: scale(0);
      animation: ripple 0.6s linear;
      background-color: rgba(255, 255, 255, 0.3);
      pointer-events: none;
    }

    @keyframes ripple {
      to {
        transform: scale(4);
        opacity: 0;
      }
    }

    /* Responsive adjustments */
    @media (max-width: 900px) {
      #app {
        padding: 15px;
      }

      .action-buttons {
        justify-content: center;
      }
    }

    @media (max-width: 768px) {
      #app {
        padding: 10px;
      }

      .navigation {
        flex-direction: column;
        align-items: stretch;
        padding: 15px 10px;
        gap: 10px;
      }

      .nav-btn {
        width: auto;
        padding: 10px 15px;
        font-size: 13px;
      }

      header {
        flex-direction: column;
        gap: 15px;
        align-items: center;
        padding: 15px 10px;
      }

      header h1 {
        font-size: 24px;
      }

      .resources {
        font-size: 16px;
        gap: 15px;
        flex-wrap: wrap;
        justify-content: center;
      }

      .resource {
        padding: 6px 12px;
        gap: 8px;
      }

      .resource-icon {
        width: 25px;
        height: 25px;
        font-size: 14px;
      }

      .probability-display {
        gap: 5px;
        justify-content: space-around;
        margin-bottom: 25px;
      }

      .probability-item {
        padding: 6px 10px;
        font-size: 12px;
        gap: 3px;
      }

      .summon-area {
        padding: 30px 0;
        gap: 25px;
      }

      .summon-btn {
        padding: 12px 30px;
        font-size: 16px;
      }

      .crystal-ball-container {
        width: 180px;
        height: 180px;
      }

      .crystal-runes {
        width: 160px;
        height: 160px;
      }

      .crystal-energy {
        width: 100px;
        height: 100px;
      }

      .spinner {
        width: 80px;
