  <script>
    // Flag to prevent multiple initializations
    let isDeepSeaUiInitialized = false;
    // Store timeouts for staggered animation
    let animationTimeouts = [];

    // ==================================================
    // ==          æ ¸å¿ƒè¾…åŠ©åŠŸèƒ½                     ==
    // ==================================================

    function showModal(type) {
      const modalId = `${type}-modal`;
      const modal = document.getElementById(modalId);
      if (modal) {
        if (type === 'interact') {
          const charNameElement = document.getElementById('placeholder-2');
          const modalCharNameElement = document.getElementById('interact-char-name');
          if (charNameElement && modalCharNameElement) {
            const name = charNameElement.textContent?.trim();
            modalCharNameElement.textContent = (name && !name.startsWith('$')) ? name : 'è§’è‰²';
          }
        }
        modal.style.display = 'flex';
        requestAnimationFrame(() => { // Use rAF for smoother transition
          modal.classList.add('active');
          const input = modal.querySelector('input[type="text"]');
          if (input) setTimeout(() => input.focus(), 50); // Short delay for focus
        });
      } else { console.error(`Modal with ID ${modalId} not found.`); }
    }

    function closeModal(modalOrId) {
      let modal = null;
      if (typeof modalOrId === 'string') {
        modal = document.getElementById(modalOrId);
      } else if (modalOrId instanceof Element) {
        modal = modalOrId;
      }

      if (modal && modal.classList.contains('active')) {
        modal.classList.remove('active');
        // Use transitionend event for hiding instead of fixed timeout
        const content = modal.querySelector('.modal-content');
        const transitionHandler = () => {
          modal.style.display = 'none';
          if (content) {
            content.removeEventListener('transitionend', transitionHandler);
          }
        };
        if (content) {
          // Listen on the content element as it has the transform transition
          content.addEventListener('transitionend', transitionHandler, { once: true });
        } else {
          // Fallback if content isn't found
          setTimeout(() => { modal.style.display = 'none'; }, 350);
        }
        // Clear potential error state on close
        const input = modal.querySelector('.modal-input');
        if (input) input.classList.remove('input-error');

      } else if (modal && modal.style.display !== 'none') {
        // If modal was somehow displayed without 'active' class, just hide it
        modal.style.display = 'none';
      }
    }


    function createRipple(event) {
      const button = event.currentTarget;
      // Ensure target is a button or has ripple effect intended
      if (!button || typeof button.getBoundingClientRect !== 'function') return;

      let existingRipple = button.querySelector('.ripple');
      if (existingRipple) { existingRipple.remove(); } // Remove previous ripple if any

      const ripple = document.createElement('span');
      const rect = button.getBoundingClientRect();
      const size = Math.max(rect.width, rect.height) * 1.5; // Make ripple larger
      // Calculate click position relative to the button
      const x = event.clientX - rect.left - size / 2;
      const y = event.clientY - rect.top - size / 2;

      ripple.style.width = ripple.style.height = `${size}px`;
      ripple.style.left = `${x}px`;
      ripple.style.top = `${y}px`;
      ripple.classList.add('ripple');

      // Append ripple and remove after animation (ensure cleanup)
      button.appendChild(ripple);
      setTimeout(() => { if (ripple.parentNode) ripple.remove(); }, 600); // Animation duration
    }

    function sendMessage(message) {
      // Basic escaping for safety within ST commands
      const escapedMessage = message.replace(/`/g, '\\`').replace(/\$/g, '\\$');
      // Simple check if it looks like a command already
      const isLikelyCommand = message.startsWith('/') || message.includes('|') || message.includes('{{') || message.includes('key=');
      // If not a command, assume it's text to send and then trigger
      const commandToSend = isLikelyCommand ? escapedMessage : `/send ${escapedMessage}|/trigger`;

      try {
        // Check if the SillyTavern function exists before calling
        if (typeof triggerSlash === 'function') {
          triggerSlash(commandToSend);
          console.log(`Sent via triggerSlash: ${commandToSend}`);
        } else {
          // Fallback for environments where triggerSlash isn't available (e.g., testing)
          console.warn('triggerSlash function not available. Simulating send:');
          console.log(`Command: ${commandToSend}`);
          // You could add alternative debug output here if needed
        }
      } catch (e) {
        console.error("Error sending command via triggerSlash:", e);
      }
    }


    function confirmAction(action) {
      sendMessage(action);
      const openModal = document.querySelector('.modal.active');
      if (openModal) closeModal(openModal.id);
    }

    function confirmActionWithInput(actionPrefix, inputId, charNamePlaceholderId) {
      const inputElement = document.getElementById(inputId);
      const modal = inputElement ? inputElement.closest('.modal') : null;

      if (!inputElement) {
        console.error(`Input element with ID ${inputId} not found.`);
        if (modal) closeModal(modal.id);
        return;
      }

      const inputValue = inputElement.value.trim();
      inputElement.classList.remove('input-error'); // Remove error class initially

      if (inputValue) {
        let message = `${actionPrefix}ï¼š${inputValue}`;
        // Special handling for interaction to include character name
        if (charNamePlaceholderId) {
          const charNameElement = document.getElementById('placeholder-2');
          const charName = charNameElement?.textContent?.trim();
          const validCharName = (charName && !charName.startsWith('$')) ? charName : 'è§’è‰²';
          // Replace the placeholder prefix with the actual action including the name
          message = `ä¸ ${validCharName} äº’åŠ¨ï¼š${inputValue}`;
        }

        sendMessage(message);
        inputElement.value = ''; // Clear input after sending
        if (modal) closeModal(modal.id);

      } else {
        // Show error state if input is empty
        inputElement.classList.add('input-error');
        // Remove error after animation completes
        setTimeout(() => { inputElement.classList.remove('input-error'); }, 500); // Match animation duration
        inputElement.focus(); // Keep focus on the input
      }
    }


    // ==================================================
    // ==       è§†è§‰æ•ˆæœå’ŒåŠ¨ç”»                     ==
    // ==================================================
    function showToast(message, duration = 3000) {
      const toast = document.getElementById('toast');
      const toastMessage = document.getElementById('toast-message');
      if (!toast || !toastMessage) {
        console.warn("Toast elements not found.");
        return;
      }

      toastMessage.textContent = message;

      // Clear any existing timer to reset the duration if shown again quickly
      if (toast.timerId) {
        clearTimeout(toast.timerId);
      }

      toast.classList.add('show');

      // Set a new timer to hide the toast
      toast.timerId = setTimeout(() => {
        toast.classList.remove('show');
        toast.timerId = null; // Clear the stored timer ID
      }, duration);
    }

    function createBackgroundEffects() {
      const container = document.body;
      const effectArea = document.getElementById('app'); // Use app area bounds
      if (!effectArea) return;

      const rect = effectArea.getBoundingClientRect();
      const numParticles = 100; // Reduced count for performance
      const numBubbles = 45;  // Reduced count

      // Clear existing particles/bubbles to prevent buildup on re-render
      container.querySelectorAll('.particle, .floating-bubble').forEach(el => el.remove());

      // Create Particles
      for (let i = 0; i < numParticles; i++) {
        const particle = document.createElement('div');
        particle.classList.add('particle');
        const size = Math.random() * 2 + 1; // Smaller size range
        particle.style.width = `${size}px`; particle.style.height = `${size}px`;
        // Constrain within app bounds + some margin
        const posX = Math.random() * rect.width + rect.left;
        const posY = Math.random() * rect.height + rect.top;
        particle.style.left = `${posX}px`; particle.style.top = `${posY}px`;
        particle.style.animationDelay = `${Math.random() * 15}s`;
        particle.style.setProperty('--angle', `${Math.random() * 360}deg`);
        container.appendChild(particle);
        // Remove after animation + buffer
        setTimeout(() => { if (particle.parentNode) particle.remove(); }, 20000);
      }

      // Create Bubbles (less frequent animation)
      for (let i = 0; i < numBubbles; i++) {
        const bubble = document.createElement('div');
        bubble.classList.add('floating-bubble');
        const size = Math.random() * 20 + 5; // Smaller size range
        bubble.style.width = `${size}px`; bubble.style.height = `${size}px`;
        const posX = Math.random() * rect.width + rect.left;
        const startY = rect.bottom + Math.random() * 50; // Start below app area
        bubble.style.left = `${posX}px`; bubble.style.top = `${startY}px`;
        bubble.style.opacity = Math.random() * 0.4 + 0.1; // More subtle
        bubble.style.setProperty('--float-duration', `${Math.random() * 10 + 10}s`); // Slower
        bubble.style.setProperty('--float-distance', `${Math.random() * 60 + 40}px`);
        bubble.style.setProperty('--float-side', `${(Math.random() - 0.5) * 40}px`);
        container.appendChild(bubble);
        animateBubbleUp(bubble, rect.top); // Pass the top boundary
      }
    }

    function animateBubbleUp(bubble, topBoundary) {
      const speed = Math.random() * 0.8 + 0.2; // Slower speed
      let currentTop = parseFloat(bubble.style.top);

      function frame() {
        currentTop -= speed;
        bubble.style.top = `${currentTop}px`;
        // Remove if it goes well above the top boundary
        if (currentTop < topBoundary - 50) {
          if (bubble.parentNode) bubble.remove();
        } else {
          requestAnimationFrame(frame);
        }
      }
      requestAnimationFrame(frame);
    }


    function createExplosion(x, y) {
      const numParticles = 60; // Reduced for performance
      const container = document.createElement('div');
      // Style for the temporary container div
      container.style.cssText = `
            position: fixed;
            left: 0;
            top: 0;
            width: 100vw;
            height: 100vh;
            pointer-events: none;
            z-index: 1001; /* Ensure it's above modals if needed */
        `;
      document.body.appendChild(container);

      for (let i = 0; i < numParticles; i++) {
        const particle = document.createElement('div');
        particle.classList.add('explosion-particle');

        const size = Math.random() * 20 + 5; // Slightly smaller max size
        particle.style.width = `${size}px`;
        particle.style.height = `${size}px`;

        const colors = ['#a0f0f8', '#ff99d6', '#d6bfff', '#f8f8f2', '#ffffff']; // Use theme colors
        const color = colors[Math.floor(Math.random() * colors.length)];

        particle.style.background = `radial-gradient(circle, ${color} 0%, transparent 70%)`;
        particle.style.boxShadow = `0 0 ${Math.random() * 8 + 4}px ${color}`; // Slightly smaller shadow

        particle.style.position = 'absolute';
        particle.style.left = `${x}px`; // Start at click point X
        particle.style.top = `${y}px`;  // Start at click point Y

        const angle = Math.random() * Math.PI * 2;
        const distance = Math.random() * 200 + 50; // Reduced max distance
        const duration = Math.random() * 1.0 + 0.5; // Slightly faster range
        const delay = Math.random() * 0.08; // Shorter max delay

        particle.style.transition = `all ${duration}s cubic-bezier(0.1, 0.8, 0.2, 1) ${delay}s`;

        container.appendChild(particle);

        // Use rAF for triggering the transition ensures the initial state is set
        requestAnimationFrame(() => {
          // Second rAF might not be necessary but can sometimes help ensure transition starts
          requestAnimationFrame(() => {
            particle.style.transform = `translate(${Math.cos(angle) * distance}px, ${Math.sin(angle) * distance}px) scale(0)`;
            particle.style.opacity = '0';
          });
        });

        // Set timeout to remove the particle element after its animation
        setTimeout(() => {
          if (particle.parentNode) particle.remove();
        }, (duration + delay) * 1000 + 100); // Add buffer
      }

      // Remove the container div after all particles should have finished
      setTimeout(() => {
        if (container.parentNode) container.remove();
      }, 2000); // Adjusted timeout based on reduced durations
    }


    function summonSequence() {
      const crystalBallContainer = document.querySelector('.crystal-ball-container');
      const resultArea = document.querySelector('.result-area');
      const summonBtn = document.querySelector('.summon-btn'); // Keep reference to button

      if (!crystalBallContainer || !resultArea || !summonBtn) {
        console.warn("Summon elements not found. Summon sequence aborted.");
        return;
      }

      // Disable button during sequence to prevent spamming
      summonBtn.disabled = true;

      // Hide results immediately if they were visible
      resultArea.classList.remove('show');
