          alert(`æ‰§è¡ŒæŒ‡ä»¤æ—¶å‡ºé”™: ${e}`); // User feedback
        }
        // Log attempt regardless for debugging
        console.log(`Attempted command: ${commandToSend}`);
      }

      // Event Listener Setup
      document.addEventListener('DOMContentLoaded', function () {
